import React, {useState, useEffect} from 'react';
import { useHistory } from 'react-router-dom';

import '../App.css';
import itemsAPI from "../api/itemsAPI";
import Item from "./_item";

const ItemsPage = () => {
  const history = useHistory();

  const [item, setItem] = useState({
    name: '',
    description: '',
    sample_class: 'sample',
    sample_type_id: 0,
    composite_sample_type_id: 0
  })

  const handleChange = async (event) => {
    setItem({
      [event.target.name]:event.target.value
    })
  }

  const handleSubmit = async (event) => {
    event.preventDefault()

    // get form data
    const form = document.querySelector('form[name="new"]');
    const data = new FormData(form);
    const formData = Object.fromEntries(data);

    // add item
    const response = await itemsAPI.addItem(formData);
    if (!response) return;

    // form errors
    if (response.errors) {
      alert('Errors\n- '+response.errors.join('\n- '))
      return;
    }
    // success
    history.push('/items')
  }

  return (
    <>
      <div className='breadcrumbs'>
        <a href="/items">Items</a>
        <span>></span>
        New Item
      </div>

      <form name="new" className="box width-400">
        <div className="flex box-title">
          <div className="flex-col-1">
            New Item
          </div>
        </div>
        <div className="plus-8"></div>

        <div className="flex box-row">
          <div className="flex-col-1">
            Name
          </div>
          <div className="flex-col-2">
            <input name="name" value={item.name} onChange={(event) => {handleChange(event)}} />
          </div>
        </div>

        <div className="flex box-row">
          <div className="flex-col-1">
            Description
          </div>
          <div className="flex-col-2">
            <input name="description" value={item.description} onChange={(event) => {handleChange(event)}} />
          </div>
        </div>

        <div className="flex box-row">
          <div className="flex-col-1">
            Type
          </div>
          <div className="flex-col-2">
            <select name="sample_class" value={item.sample_class} onChange={(event) => {handleChange(event)}}>
              <option value="sample">Sample</option>
              <option value="composite_sample">Composite Sample</option>
            </select>
          </div>
        </div>

        <div className="flex box-row">
          <div className="flex-col-1 center">
            <button onClick={(event) => handleSubmit(event)}>Submit</button>
          </div>
        </div>
        <div className="plus-8"></div>
      </form>
    </>
  );
}

export default ItemsPage;
