import React, {useState, useEffect} from 'react';
import { useHistory } from 'react-router-dom';

import '../App.css';
import itemsAPI from "../api/itemsAPI";
import sampleTypesAPI from "../api/sampleTypesAPI";
import locationsAPI from "../api/locationsAPI";
import containersAPI from "../api/containersAPI";

const ContainerForm = () => {
  const history = useHistory();

  const [container, setContainer] = useState({
    name: '',
    description: '',
    sample_class: 'sample',
    sample_type_id: 0,
    composite_sample_type_id: 0
  })
  const [sampleTypes, setSampleTypes] = useState([])
  const [locations, setLocations] = useState([])
  const [containers, setContainers] = useState([])

  useEffect(() => {
    const init = async () => {
      // get sample types and composite sample types
      const response1 = await sampleTypesAPI.getSampleTypes();
      const response2 = await locationsAPI.getLocations();
      const response3 = await containersAPI.getContainers();
      if (!response1 || !response2 || !response3 ) return;

      // success
      setSampleTypes(response1);
//       setLocations(response2);
      setContainers(response3);
debugger;
    };

    init();
  }, []);

  const handleChange = async (event) => {
    setContainer({
      [event.target.name]:event.target.value
    })
  }

  const handleSubmit = async (event) => {
    event.preventDefault()

    // get form data
    const form = document.querySelector('form[name="new"]');
    const data = new FormData(form);
    const formData = Object.fromEntries(data);

    // add container
    const response = await containersAPI.addContainer(formData);
    if (!response) return;

    // form errors
    if (response.errors) {
      alert('Errors\n- '+response.errors.join('\n- '))
      return;
    }
    // success
    history.push('/containers')
  }

  return (
    <>
      <div className='breadcrumbs'>
        <a href="/containers">Containers</a>
        <span>></span>
        New Container
      </div>

      <form name="new" className="box width-400">
        <div className="flex box-title">
          <div className="flex-col-1">
            New Container
          </div>
        </div>
        <div className="plus-8"></div>

        <div className="flex box-row">
          <div className="flex-col-1">
            Name
          </div>
          <div className="flex-col-2">
            <input name="name" value={container.name} autoComplete = "off" onChange={(event) => {handleChange(event)}} />
          </div>
        </div>

        <div className="flex box-row">
          <div className="flex-col-1">
            Description
          </div>
          <div className="flex-col-2">
            <input name="description" value={container.description} autoComplete = "off" onChange={(event) => {handleChange(event)}} />
          </div>
        </div>

        <div className="flex box-row">
          <div className="flex-col-1">
            Container Type
          </div>
          <div className="flex-col-2">
            <select name="container_type_id" value={container.container_type_id} onChange={(event) => {handleChange(event)}}>
              <option value="0">Select... &nbsp;</option>
              {sampleTypes.map((sampleType) => (
                <option value={sampleType.id} key={sampleType.id}>{sampleType.name}</option>
              ))}
            </select>
          </div>
        </div>

        <div className="flex box-row">
          <div className="flex-col-1">
            Row
          </div>
          <div className="flex-col-2">
            <input name="row" value={container.row} autoComplete = "off" onChange={(event) => {handleChange(event)}} />
          </div>
        </div>

        <div className="flex box-row">
          <div className="flex-col-1">
            Column
          </div>
          <div className="flex-col-2">
            <input name="column" value={container.column} autoComplete = "off" onChange={(event) => {handleChange(event)}} />
          </div>
        </div>

        <div className="flex box-row">
          <div className="flex-col-1">
            Location
          </div>
          <div className="flex-col-2">
            <select name="location_id" value={container.location_id} onChange={(event) => {handleChange(event)}}>
              <option value="0">Select... &nbsp;</option>
              {locations.map((location) => (
                <option value={location.id} key={location.id}>{location.name}</option>
              ))}
            </select>
            <div className="plus-8"></div>
            <input name="location" value={container.location} autoComplete = "off" onChange={(event) => {handleChange(event)}} />
          </div>
        </div>

        <div className="flex box-row">
          <div className="flex-col-1 center">
            <button onClick={(event) => handleSubmit(event)}>Submit</button>
          </div>
        </div>
        <div className="plus-8"></div>
      </form>
    </>
  );
}

export default ContainerForm;
