import axiosInstance from './axiosInstance';

const itemsAPI = {};

// get items
itemsAPI.getItems = () => axiosInstance
  .get('/api/v1/items')
  .then((response) => response.data)
  .catch((error) => error);

// get item
itemsAPI.getItem = (id) => axiosInstance
  .get(`/api/v1/items/${id}`)
  .then((response) => response.data)
  .catch((error) => error);

// add item
itemsAPI.addItem = (name, description) => axiosInstance
  .post('/api/v1/items/', {
    item: {
      name: name,
      description: description
    }
  })
  .then((response) => response.data)
  .catch((error) => error);

export default itemsAPI;
