import React, {useState, useEffect} from 'react';

import '../App.css';
import samplesAPI from "../api/samplesAPI";
import Sample from "./_sample";

const SamplePage = ({match}) => {
  // default members to empty array
  const sampleId = match.params.id
  const [sample, setSample] = useState({});

  useEffect(() => {
    const init = async () => {
      // get members
      const response = await samplesAPI.getSample(sampleId);
      if (!response) return;

      // success
      setSample(response);
    };

    init();
  }, []);

  const editSample = () => {
    alert('TODO: Edit Sample')
  }

  return (
    <>
      <div class='breadcrumbs'>
        <a href="/samples">Samples</a>
        <span>></span>
        {sample.name}
        <span>></span>
        <button onClick={() => editSample()}>Edit</button>
      </div>

      <div className="box width-400">
        <div className="flex box-title">
          <div className="flex-col-1">
            {sample.name}
          </div>
        </div>
        <div className="plus-8"></div>

        <div className="flex box-row">
          <div className="flex-col-1">
            Name
          </div>
          <div className="flex-col-2">
            {sample.name}
          </div>
        </div>

        <div className="flex box-row">
          <div className="flex-col-1">
            Description
          </div>
          <div className="flex-col-2">
            {sample.description}
          </div>
        </div>

        <div className="plus-8"></div>
      </div>
    </>
  );
}

export default SamplePage;

