import axiosInstance from './axiosInstance';

const API = {};

// getSamples
API.getSamples = () => axiosInstance
  .get('samples')
  .then((response) => response.data)
  .catch((error) => error);

// getSample
API.getSample = (id) => axiosInstance
  .get(`samples/${id}`)
  .then((response) => response.data)
  .catch((error) => error);

// addSample
API.addSample = (formData) => axiosInstance
  .post('samples/', {
    sample: formData
  })
  .then((response) => response.data)
  .catch((error) => error);

export default API;
