import axiosInstance from './axiosInstance';

const memberAPI = {};

// get members
memberAPI.getMembers = () => axiosInstance
  .get('/members')
  .then((response) => response.data)
  .catch((error) => error);

// get member
memberAPI.getMember = (id) => axiosInstance
  .get(`/members/${id}`)
  .then((response) => response.data)
  .catch((error) => error);

// add friend
memberAPI.addFriend = (id, friend_id) => axiosInstance
  .post('/friendships/', {
    friendship: {
      member_id: id,
      friend_id: friend_id
    }
  })
  .then((response) => response.data)
  .catch((error) => error);

// add member
memberAPI.addMember = (first_name, last_name, url) => axiosInstance
  .post('/members/', {
    member: {
      first_name: first_name,
      last_name: last_name,
      url: url
    }
  })
  .then((response) => response.data)
  .catch((error) => error);

// find introductions

memberAPI.findIntroductions = (id, text) => axiosInstance
  .post(`/members/${id}/find_introductions`, {
    text: text
  })
  .then((response) => response.data)
  .catch((error) => error);

export default memberAPI;
