import axiosInstance from './axiosInstance';

const ___API = {};

// get items
___API.getItems = () => axiosInstance
  .get('/items')
  .then((response) => response.data)
  .catch((error) => error);

// get item
___API.getItem = (id) => axiosInstance
  .get(`/items/${id}`)
  .then((response) => response.data)
  .catch((error) => error);

// add item
___API.addItem = (name, description) => axiosInstance
  .post('/items/', {
    item: {
      name: name,
      description: description
    }
  })
  .then((response) => response.data)
  .catch((error) => error);

export default ___API;
