import axiosInstance from './axiosInstance';

const API = {};

// getContainers
API.getSampleTypeCategories = () => axiosInstance
  .get('container_type_categories')
  .then((response) => response.data)
  .catch((error) => error);

// getContainer
API.getSampleTypeCategory = (id) => axiosInstance
  .get(`container_type_categories/${id}`)
  .then((response) => response.data)
  .catch((error) => error);

// addContainer
API.addSampleTypeCategory = (formData) => axiosInstance
  .post('container_type_categories/', {
    container: formData
  })
  .then((response) => response.data)
  .catch((error) => error);

export default API;
