import React, {useState, useEffect} from 'react';
import { useHistory } from 'react-router-dom';

import '../App.css';
import itemsAPI from "../api/itemsAPI";
import cmpositeSampleTypeCategoriesAPI from "../api/cmpositeSampleTypeCategoriesAPI";
import compositeCompositeSampleTypesAPI from "../api/compositeCompositeSampleTypesAPI";
import cmpositeSampleTypesAPI from "../api/cmpositeSampleTypesAPI";

const CompositeSampleTypeForm = () => {
  const history = useHistory();

  const [cmpositeSampleType, setCompositeSampleType] = useState({
    name: '',
    description: '',
    sample_class: 'sample',
    sample_type_id: 0,
    composite_sample_type_id: 0
  })
  const [cmpositeSampleTypeCategories, setCompositeSampleTypeCategories] = useState([])
  const [cmpositeSampleTypes, setCompositeSampleTypes] = useState([])

  useEffect(() => {
    const init = async () => {
      // get sample types and composite sample types
      const response1 = await cmpositeSampleTypesAPI.getCompositeSampleTypes();
      const response3 = await cmpositeSampleTypeCategoriesAPI.getCompositeSampleTypeCategories();
      if (!response1 || !response3 ) return;

      // success
      setCompositeSampleTypes(response1);
      setCompositeSampleTypeCategories(response3);
    };

    init();
  }, []);

  const handleChange = async (event) => {
    setCompositeSampleType({
      [event.target.name]:event.target.value
    })
  }

  const handleSubmit = async (event) => {
    event.preventDefault()

    // get form data
    const form = document.querySelector('form[name="new"]');
    const data = new FormData(form);
    const formData = Object.fromEntries(data);

    // add cmpositeSampleType
    const response = await cmpositeSampleTypesAPI.addCompositeSampleType(formData);
    if (!response) return;

    // form errors
    if (response.errors) {
      alert('Errors\n- '+response.errors.join('\n- '))
      return;
    }
    // success
    history.push('/sample_types')
  }

  return (
    <>
      <div className='breadcrumbs'>
        <a href="/cmpositeSampleTypes">CompositeSampleTypes</a>
        <span>></span>
        New CompositeSampleType
      </div>

      <form name="new" className="box width-box">
        <div className="flex box-title">
          <div className="flex-col-1">
            New CompositeSampleType
          </div>
        </div>
        <div className="plus-8"></div>

        <div className="flex box-row">
          <div className="flex-col-1">
            Name
          </div>
          <div className="flex-col-2">
            <input name="name" value={cmpositeSampleType.name} autoComplete = "off" onChange={(event) => {handleChange(event)}} />
          </div>
        </div>

        <div className="flex box-row">
          <div className="flex-col-1">
            Description
          </div>
          <div className="flex-col-2">
            <input name="description" value={cmpositeSampleType.description} autoComplete = "off" onChange={(event) => {handleChange(event)}} />
          </div>
        </div>

        <div className="flex box-row">
          <div className="flex-col-1">
            Sample Type Category
          </div>
          <div className="flex-col-2">
            <select name="sample_type_category_id" value={cmpositeSampleType.sample_type_category_id} onChange={(event) => {handleChange(event)}}>
              <option value="0">Select... &nbsp;</option>
              {cmpositeSampleTypeCategories.map((cmpositeSampleTypeCategory) => (
                <option value={cmpositeSampleTypeCategory.id} key={cmpositeSampleTypeCategory.id}>{cmpositeSampleTypeCategory.name}</option>
              ))}
            </select>
          </div>
        </div>

        <div className="flex box-row">
          <div className="flex-col-1 center">
            <button onClick={(event) => handleSubmit(event)}>Submit</button>
          </div>
        </div>
        <div className="plus-8"></div>
      </form>
    </>
  );
}

export default CompositeSampleTypeForm;

