import React, {useState, useEffect} from 'react';

import '../App.css';
import backendAPI from "../api/backendAPI";
import Item from "./_item";

const ItemPage = () => {
  // default members to empty array
  const [items, setItems] = useState([]);

  const [itemName, setItemName] = useState('');
  const [itemDescription, setItemDescription] = useState('');

  useEffect(() => {
    const init = async () => {
      // get members
      const response = await backendAPI.getItems();
      if (!response) return;

      // success
      setItems(response);
    };

    init();
  }, []);

  const handleSubmit = async () => {
    // add item
    const response = await backendAPI.addItem(itemName, itemDescription);
    if (!response) return;

    // form errors
    if (response.errors) {
      alert('Errors\n- '+response.errors.join('\n- '))
      return;
    }

    // success
    window.location.reload(true);
  }

  return (
    <>
      <div class='breadcrumbs'>
        Items
      </div>

      <div className="flex flex-title">
        <div className="flex-col-1">
          Name
        </div>
        <div className="flex-col-2">
          Description
        </div>
      </div>

      {items.map((item) => (
        <Item item={item} />
      ))}
      <div class="plus-16"></div>

      <div class="box width-400">
        <div className="flex flex-title-box">
          <div className="flex-col-1">
            New Item
          </div>
        </div>
        <div className="plus-8"></div>
        <div className="flex flex-row-box">
          <div className="flex-col-1">
            Name
          </div>
          <div className="flex-col-2">
            <input value={itemName} onChange={(event) => {setItemName(event.target.value)}} />
          </div>
        </div>
        <div className="flex flex-row-box">
          <div className="flex-col-1">
            Description
          </div>
          <div className="flex-col-2">
            <input value={itemDescription} onChange={(event) => {setItemDescription(event.target.value)}} />
          </div>
        </div>
        <div className="padding-8 center">
          <button onClick={handleSubmit}>Submit</button>
        </div>
        <div className="plus-8"></div>
      </div>
    </>
  );
}

export default ItemPage;
