import React, {useState, useEffect} from 'react';

import '../App.css';
import backendAPI from "../api/backendAPI";
import Member from "./_member";

const App = () => {
  // default members to empty array
  const [members, setMembers] = useState([]);

  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [url, setURL] = useState('');

  useEffect(() => {
    const init = async () => {
      // get members
      const response = await backendAPI.getMembers();
      if (!response) return;

      // success
      setMembers(response);
    };

    init();
  }, []);

  const handleSubmit = async () => {
    // add member
    const response = await backendAPI.addMember(firstName, lastName, url);
    if (!response) return;

    // form errors
    if (response.errors) {
      alert('Errors\n- '+response.errors.join('\n- '))
      return;
    }

    // success
    window.location.reload(true);
  }

  return (
    <>
      <div class='breadcrumbs'>
        Members
      </div>

      <div className="flex flex-title">
        <div className="flex-col-1">
          Member
        </div>
        <div className="flex-col-2">
          Short URL
        </div>
        <div className="flex-col-1">
          Friends
        </div>
      </div>

      {members.map((member) => (
        <Member member={member} />
      ))}
      <div class="plus-16"></div>

      <div class="box width-400">
        <div className="flex flex-title-box">
          <div className="flex-col-1">
            New member
          </div>
        </div>
        <div className="plus-8"></div>
        <div className="flex flex-row-box">
          <div className="flex-col-1">
            First Name
          </div>
          <div className="flex-col-2">
            <input value={firstName} onChange={(event) => {setFirstName(event.target.value)}} />
          </div>
        </div>
        <div className="flex flex-row-box">
          <div className="flex-col-1">
            Last Name
          </div>
          <div className="flex-col-2">
            <input value={lastName} onChange={(event) => {setLastName(event.target.value)}} />
          </div>
        </div>
        <div className="flex flex-row-box">
          <div className="flex-col-1">
            URL
          </div>
          <div className="flex-col-2">
            <input value={url} onChange={(event) => {setURL(event.target.value)}} />
          </div>
        </div>
        <div className="padding-8 center">
          <button onClick={handleSubmit}>Submit</button>
        </div>
        <div className="plus-8"></div>
      </div>
    </>
  );
}

export default App;
