import axiosInstance from './axiosInstance';

const itemsAPI = {};

// get items
itemsAPI.getItems = () => axiosInstance
  .get('items')
  .then((response) => response.data)
  .catch((error) => error);

// get item
itemsAPI.getItem = (id) => axiosInstance
  .get(`items/${id}`)
  .then((response) => response.data)
  .catch((error) => error);

// add item
itemsAPI.addItem = (formData) => axiosInstance
  .post('items/', {
    item: formData
  })
  .then((response) => response.data)
  .catch((error) => error);

export default itemsAPI;
