import React, {useState, useEffect} from 'react';
import { useHistory } from 'react-router-dom';

import '../App.css';
import itemsAPI from "../api/itemsAPI";
import sampleTypesAPI from "../api/sampleTypesAPI";
import compositeSampleTypesAPI from "../api/compositeSampleTypesAPI";
import locationsAPI from "../api/locationsAPI";

// traditional javascript variable
var x = 0

const LocationForm = () => {
  const history = useHistory();

  const [location, setLocation] = useState({
    name: '',
    description: '',
    sample_class: 'sample',
    sample_type_id: 0,
    composite_sample_type_id: 0
  })
  const [sampleTypes, setSampleTypes] = useState([])
  const [compositeSampleTypes, setCompositeSampleTypes] = useState([])
  const [locations, setLocations] = useState([])
  const [labelList, setLabelList] = useState([])
  const [labelInputs, setLabelInputs] = useState({})
  const [sizeInputs, setSizeInputs] = useState({})

  useEffect(() => {
    const init = async () => {
      // get sample types and composite sample types
      const response1 = await sampleTypesAPI.getSampleTypes();
      const response2 = await compositeSampleTypesAPI.getCompositeSampleTypes();
      const response3 = await locationsAPI.getLocations();
      if (!response1 || !response2 || !response3 ) return;

      // success
      setSampleTypes(response1);
      setCompositeSampleTypes(response2);
      setLocations(response3);
    };

    init();
  }, []);

  const handleChange = async (event) => {
    setLocation({
      [event.target.name]:event.target.value
    })
  }

  const handleAdd = async (event) => {
    event.preventDefault();

    // await setX() - would need to turn the result into a promise
    x += 1
    setLabelList([...labelList, x])
  }

  const handleLabelById = async (event) => {
    setLabelInputs({
      [event.target.id]:event.target.value
    })
  }

  const handleSizeById = async (event) => {
    setSizeInputs({
      [event.target.id]:event.target.value
    })
  }

  const handleSubmit = async (event) => {
    event.preventDefault()

    // get form data
    const form = document.querySelector('form[name="new"]');
    const data = new FormData(form);
    const formData = Object.fromEntries(data);

    // add location
    const response = await locationsAPI.addLocation(formData);
    if (!response) return;

    // form errors
    if (response.errors) {
      alert('Errors\n- '+response.errors.join('\n- '))
      return;
    }
    // success
    history.push('/locations')
  }

  return (
    <>
      <div className='breadcrumbs'>
        <a href="/locations">Locations</a>
        <span>></span>
        New Location
      </div>

      <form name="new" className="box width-box">
        <div className="flex box-title">
          <div className="flex-col-1">
            New Location
          </div>
        </div>
        <div className="plus-8"></div>

        <div className="flex box-row">
          <div className="flex-col-1">
            Name
          </div>
          <div className="flex-col-2">
            <input name="name" value={location.name} autoComplete = "off" onChange={(event) => {handleChange(event)}} />
          </div>
        </div>

        <div className="flex box-row">
          <div className="flex-col-1">
            Description
          </div>
          <div className="flex-col-2">
            <input name="description" value={location.description} autoComplete = "off" onChange={(event) => {handleChange(event)}} />
          </div>
        </div>

        <div className="divider"></div>

        <div className="flex box-row">
          <div className="flex-col-1">
            Specification (JSON for now)
          </div>
          <div className="flex-col-2">
            <textArea name="specification" onChange={(event) => {handleChange(event)}}> {location.specification} </textArea>
          </div>
        </div>

        {labelList.map((loc) => (
          <div className="flex box-row" id={`div_${loc}`}>
            <div className="flex-col-1">
              Label / Size
            </div>
            <div className="flex-col-1">
              <input className="labels" id={`label_${loc}`} onChange={(event) => {handleLabelById(event)}} />
            </div>
            <div className="flex-col-1">
              <input className="sizes" id={`size_${loc}`} onChange={(event) => {handleSizeById(event)}} />
            </div>
          </div>
        ))}

        <div className="flex box-row">
          <div className="flex-col-1">
          </div>
          <div className="flex-col-2">
            <button onClick={(event) => handleAdd(event)}>Add Level</button>
          </div>
        </div>

        <div className="divider"></div>

        <div className="flex box-row">
          <div className="flex-col-1 center">
            <button onClick={(event) => handleSubmit(event)}>Submit</button>
          </div>
        </div>
        <div className="plus-8"></div>
      </form>
      <div className="plus-8"></div>
      <div dangerouslySetInnerHTML={{__html: ' \
        specification: [ \
          { \
            label: &lt;string>, \
            size: &lt;int> \
          }, \
          ... \
        ] \
      '}} />
      <div className="plus-8"></div>
      <div dangerouslySetInnerHTML={{__html: ' \
        example: [ \
          { \
            label: \'box\', \
            size:80 \
          }, \
          { \
            label: \'slot\', \
            size:16 \
          } \
        ] \
      '}} />
    </>
  );
}
export default LocationForm;

